window.fbAsyncInit = function() {
	FB.init({ 
		appId: 'YOUR_APP_ID', 
		status: true, 
		cookie: true,
		xfbml: true,
		oauth: true
	});
};
function SimpleFacebookOauth(){this.login=function(e,t){FB.login(function(e){t(e.authResponse,e.authResponse.accessToken)},e)};this.getMe=function(e){FB.api("/me",function(t){e({id:t.id,name:t.name,first_name:t.first_name,last_name:t.last_name,email:t.email,picture:"https://graph.facebook.com/"+t.id+"/picture/?type=large"})})};this.publish=function(e,t){FB.api("/me/feed","POST",e,function(e){t()})};this.checkLogin=function(e){FB.getLoginStatus(function(t){if(t.status==="connected"){e({success:true,id:t.authResponse.userID,token:t.authResponse.accessToken})}else{e({success:false})}})}}